mapper.select:
select ID
  from USER
 where ID=#{id}

select EMAIL
  from USER
 where EMAIL=#{email}

select NICKNAME
  from USER
 where ID=#{id}

select PASSWORD
  from USER
 where ID=#{id}

select ID 
  from USER
 where EMAIL = #{email}

select NICKNAME
  from USER
 where EMAIL = #{email} 
   and ID = #{id}

mapper.update:

update USER
   set SUBSCRIBE_YN="Y", PAID_M=PAID_M+${months}, EXPIRATION_DATE=(date_add(NOW(),interval ${months} month))
 where USER_ID = (select U.USER_ID from (select USER_ID from USER where ID="${ID}") AS U);

update USER
   set PAID_M=PAID_M+${months}, EXPIRATION_DATE=(date_add(NOW(),interval ${months} month))
 where USER_ID = (select U.USER_ID from (select USER_ID from USER where ID="${ID}") AS U);

update USER
   set PASSWORD=#{password}
 where ID = #{id}

mapper.select:
select ID, EMAIL, NICKNAME, PHONE_NUM, EXPIRATION_DATE
  from USER
 where ID=#{user_id}


select PAID_DATE
  from PAID
 where date_format(PAID_DATE, '%Y%m%d') = date_format(now(), '%Y%m%d');

mapper.update:

update PAID
   set AMOUNT=AMOUNT+#{months}
 where date_format(PAID_DATE, '%Y%m%d') = date_format(now(), '%Y%m%d')

mapper.select:

select VISIT_DATE
  from VISIT
 where date_format(VISIT_DATE, '%Y%m%d') = date_format(now(), '%Y%m%d');

